    </div>
  </div>
</template>

<script>

  import image from '../assets/Screen Shot 2019-02-13 at 18.22.54.png'
  import friends from '../assets/fiends.jpeg'
export default {
  name: "DraggableImage",
  data() {
    return {
      image,
        friends
    };
  },
  methods: {
    onDragged({ el, deltaX, deltaY, first, last }) {
      const l = +window.getComputedStyle(el)["left"].slice(0, -2) || 0;
      const t = +window.getComputedStyle(el)["top"].slice(0, -2) || 0;
      el.style.left = l + deltaX + "px";
      el.style.top = t + deltaY + "px";
    }
  }
};
</script>

